#include <Wire.h>
 #include <MPU6050.h>
 MPU6050 accelerometer;
 void setup() {
 Serial.begin(115200);
 Serial.println("Initializing the MPU6050 sensor. Wait for a while");
 delay(2000);
 while(!accelerometer.begin(MPU6050_SCALE_2000DPS, MPU6050_RANGE_2G)) {
 Serial.println("Failed to read from the sensor. Something is Wrong!");
 delay(500);
 }
 }
 void loop() {
 Vector sensor_data = accelerometer.readNormalizeAccel();
 int pitch_value = -(atan2(sensor_data.XAxis, sqrt(sensor_data.YAxis*sensor_data.YAxis + sensor_data.ZAxis*sensor_data.ZAxis))*180.0)/M_PI;
 Serial.print(" Pitch = ");
 Serial.print(pitch_value);
 int roll_value = (atan2(sensor_data.YAxis, sensor_data.ZAxis)*180.0)/M_PI;
 Serial.print(" Roll = ");
 Serial.println(roll_value);
 delay(10);
 }

#define pirPin 2
 int calibrationTime = 30;
 long unsigned int lowIn;
 long unsigned int pause = 5000;
 boolean lockLow = true;
 boolean takeLowTime;
 int PIRValue = 0;
 void setup() {
 Serial.begin(9600);
 pinMode(pirPin, INPUT);
 }
 void loop() {
 PIRSensor();
 }
 void PIRSensor() {
 if(digitalRead(pirPin) == HIGH) {
 if(lockLow) {
 PIRValue = 1;
 lockLow = false;
 Serial.println("Motion detected.");
 delay(50);
 }
 takeLowTime = true;
 }
 if(digitalRead(pirPin) == LOW) {
 if(takeLowTime){
lowIn = millis();
takeLowTime = false;
}
 if(!lockLow && millis() - lowIn > pause) {
 PIRValue = 0;
 lockLow = true;
 Serial.println("Motion ended.");
 delay(50);
 }
 }
 }

