const int dataIN = 2;
 //IR sensor INPUT unsigned
 long prevmillis;
 // To store time unsigned
 long duration;
 // To store time difference
 unsigned long refresh;
 // To store time for refresh of reading
 int rpm;
 // RPM value 
 boolean currentstate;
 // Current state of IR input scan
 boolean prevstate;
 // State of IR sensor in previous scan 
 void setup() {
 pinMode(dataIN,INPUT);
 prevmillis = 0;
 prevstate = LOW;
 Serial.begin(9600);
 }
 void loop() {
 currentstate = digitalRead(dataIN);
 if( prevstate != currentstate) {
 if( currentstate == HIGH ) {
 duration = ( micros() - prevmillis );
 rpm = (60000000/duration);
 prevmillis = micros();
 }
 }
 prevstate = currentstate;
 // LCD Display
 if( ( millis()-refresh ) >= 100 ) {
 Serial.println(rpm);
 }
 }

