LiquidCrystal_I2C lcd(0x27,16,2);
#define analogPin A0
#define beta 3950
#define resistance 10
void setup()
{
 lcd.init();
 lcd.backlight();
 Serial.begin(9600);
}
void loop()
{
 long a =1023 - analogRead(analogPin);
 Serial.print("Raw reading ");
 Serial.println(a);
 float tempC = beta /(log((1025.0 * 10 / a - 10) / 10) + beta / 298.0) - 273.0;
 float tempF = 1.8*tempC + 32.0;
 Serial.print("Centigrade ");
 Serial.println(tempC);
 Serial.print("Fahrenheit ");
 Serial.println(tempF);
 Serial.println("");
 lcd.setCursor(0, 0);
 lcd.print("Temp: ");
 lcd.print(tempC);
 lcd.write(char(223));
 lcd.print("C");
 lcd.setCursor(0, 1);
 lcd.print("Fahr: ");
 lcd.print(tempF);
 lcd.write(char(223));
 lcd.print(" F");F"
 delay(200);
}

